INSERT INTO `total` VALUES (97, 'https://img1.winxuancdn.com/3263/1201883263_0_0_800x800.jpg?1570687161410&imageMogr2/thumbnail/160x160', '认识车标', '企业采购书卡&书目服务电话（微信同号）178-8231-4046（法定工作日9:00-17:00）', '10', '5', '5', '王伟文', '童书育儿', NULL, '《儿童学习用表·认识车标》无目录', '无', '<p>定价：为出版社全国统一定价；</p><p>文轩价：为商品的销售价，是您最终决定是否购买商品的依据；受系统缓存影响，最终价格以商品放入购物车后显示的价格为准；</p>', '《奇妙的蛋蛋书》主要侧重0-6岁幼儿的认知启蒙、语言启蒙、国学启蒙和全脑开发。由谭树辉主编的《左脑训练/奇妙的蛋蛋书》内容主要以语言启蒙为主，主要分为字词练习和短句练习，具体分为学象声词、学表达行为、学会表达情绪、学会礼貌用语，紧扣牙牙学语的宝宝的说话特点、紧贴宝宝的表达需求，内容丰富，采用形象逼真，颜色鲜艳的高清实物图，加深宝宝对话语的理解。', '趣味童书', '二十一世纪出版社集团', '2018年7月\r\n', NULL);
