INSERT INTO `total` VALUES (101, 'https://img0.winxuancdn.com/3412/1201673412_0_1_800x800.jpg?1527039601129&imageMogr2/thumbnail/160x160', '金葫芦宝宝快乐学习卡·无图识字2', '企业采购书卡&书目服务电话（微信同号）178-8231-4046（法定工作日9:00-17:00）', '15', '5', '3', '安韶', '童书育儿', NULL, '《无图识字1、2、3》：无图识字&nbsp;&nbsp;&nbsp;集中注意&nbsp;&nbsp;&nbsp;快速学习；规范书写&nbsp;&nbsp;&nbsp;组词造句&nbsp;&nbsp;&nbsp;全面掌握', '无', '<p>定价：为出版社全国统一定价；</p><p>文轩价：为商品的销售价，是您最终决定是否购买商品的依据；受系统缓存影响，最终价格以商品放入购物车后显示的价格为准；</p>', '儿童学习用表，全套共10张，内容分别是汉语拼音、汉字基础、识字、数字、加减法口算表、乘法口诀表、小学数学用表、英语字母表、标准视力对照表、常用标志。该系列学习用表内容涵盖小学语数英等课程基本常用知识，是亲子共读和小学生课堂学习用表的理想选择。', '趣味童书', '安徽少年儿童出版社', '2019年6月\r\n', NULL);
